                      id="exampleInputEmail1"
                      placeholder="Email"
                    />
                  </div>
                  <div className="resend">
                    {timerStart ? <OtpTimer seconds= {5} minutes={0} resend={handleClick} ButtonText="Resend" /> :  <div  onClick={startTimer}> Resend</div>}

      </div>



